import React from 'react'
import './Main.css'
import main1img from '../Utilities/logo.jpg'

const AQI  = fetch('https://api.windy.com/fdxSjMZoGDyfOPE04ZWDf93vuiEG62oL/point-forecast/v2');

function hideloader() {
  document.getElementById('loading').style.display = 'none';
}

async function getapi(AQI) {
    
  // Storing response
  const response = await fetch(AQI);
  
  // Storing data in form of JSON
  var data = await response.json();
  console.log(data);
  if (response) {
      hideloader();
  }
  show(data);
}
function Main() { 
  return (
    <div className='main'> 


      <div className='main1'>
        <div className='main1Box'>
          <div className='main1box-1'>
            <p className='mainPara'>Welcome to Urban resolve...<br></br>
            let's make our city more livable by reporting all the issues...</p>
            <button className='main1Button'>Report an issue</button>
          </div>
          <div className='main1Box-2'>
            <img className='mainImg' src={main1img} alt='main page logo' ></img>
          </div>
        </div>
      </div>

      <div className='main2'>

        <h1>Check the AQI of your city</h1>
        <input className='searchAQI' placeholder='Enter Location. . .'></input>
        {/* console.log(AQI); */}
      </div>


      <div className='main3'>

      <h1>News related to pollution and clean energy</h1>

      </div>


      <div className='main4'>

      <h1>Insights into preserving the planet by reducing carbon footprint</h1>

      </div>


    </div>
  )
}

export default Main
