import React, {ReactDOM} from "react";

const { render } = ReactDOM;

const noop = () => {};

function create(Component, props) {
  return new Promise((resolve, reject) => {
    const root = document.body;
    const wrapper = document.getElementById('modal');
   
    const cleanup = () => {
      ReactDOM.unmountComponentAtNode(wrapper);
      root.classList.remove('no-overflow');
      //wrapper.remove();
    };
    
    root.classList.add('no-overflow');
    
    const response = func => response => { cleanup(); func(response); };
    
    const viewProps = { accept: response(resolve), close: response(reject) };
    
    ReactDOM.render(<Component {...props} {...viewProps} />, wrapper);
  });
}

class Modal extends React.Component {
  constructor(props) {
    super(props);
    const { accept, close } = this.props;
    this.y = this.close.bind(this, accept, 'accept');
    this.x = this.close.bind(this, close, 'close');
    this.state = { classname: 'modal' };
  }
  close(cb, response) {
    this.setState({ classname: 'modal out' });
    setTimeout(() => cb(response), 400);
  }
  render() {
		const { close, accept, children } = this.props;
    const { classname: modal } = this.state;

		return (
			<div className="backdrop" onClick={this.x}>
				<div className={modal} onClick={e => e.stopPropagation()}>
          <div className="close-mark" onClick={this.x}>x</div>
          <div className="content">
						{ children }
            <button onClick={this.y}>solve!</button>
          </div>
				</div>
			</div>
		)
	}
}

const ModalContent = (
  <div>
    <h1>React Modal</h1>
    <h2>Using JS Promises</h2>
    <p>You can accept/reject data.</p>
  </div>
);

class App extends React.Component {
  constructor(props) {
    super(props);
    this.openModal = this.openModal.bind(this);
    this.state = { value: '' };
  }
  componentDidMount() {
    this.openModal();
  }
  openModal() {
    this.setState({ value: 'waiting...' });
    create(Modal, { children: ModalContent })
      .then(res => this.setState({ value: res }))
      .catch(err => this.setState({ value: err }));
  }
  render() {
    return (
      <div className="app-container">
        <button onClick={this.openModal}>Open Modal</button>
        <div>{this.state.value}</div>
      </div>
    );
  }
}
