import React, { useEffect, useState } from "react";
import "./News.css";
// https://newsapi.org/v2/everything?q=pollutions&apikey=226cb84b76db43dcb0b55516711accb2

function News() {

  const url ="https://newsapi.org/v2/everything?q=pollutions&apikey=226cb84b76db43dcb0b55516711accb2";

  const [articles, setArticles] = useState([]);

  const news = async () => {
    try {
      const response = await fetch(url);
      const data = await response.json();
      setArticles(data);
      displayWeather(data);
    } catch (error) {
      console.log(error);
    }
  };

   function displayWeather(articles) {
    const { title } = articles;
    document.querySelector(".title").innerText = articles;
  }

  useEffect(() => {
    news();
  }, []);
  console.log(articles);

  return (
    <>
    <div className='title'>{title}</div>
    </>
  );
}

export default News;
