import React, { useEffect, useState } from "react";
import "./News.css";
// https://newsapi.org/v2/everything?q=pollutions&apikey=226cb84b76db43dcb0b55516711accb2

function News() {

  const url ="https://newsapi.org/v2/everything?q=pollutions&apikey=226cb84b76db43dcb0b55516711accb2";

  const [articles, setArticles] = useState([]);

  const news = async () => {
    try {
      const response = await fetch(url);
      const data = await response.json();
      setArticles(data);
    } catch (error) {
      console.log(error);
    }
  };

   function displayWeather(data) {
    const { name } = data;
    const { description } = data.weather[0];
    const { temp, humidity } = data.main;
    const { speed } = data.wind;
    document.querySelector(".city").innerText = "Weather in " + name;
    document.querySelector(".desc").innerText = "Feels like " + description;
    document.querySelector(".temp").innerText = temp + "°C";
    document.querySelector(".humidity").innerText =
      "Humidity: " + humidity + "%";
    document.querySelector(".windSpeed").innerText =
      "Wind speed: " + speed + " km/h";
    document.querySelector(".weather").classList.remove("loading")
  }

  useEffect(() => {
    news();
  }, []);
  console.log(articles);

  return (
    <>
    </>
  );
}

export default News;
