import React, {useEffect, useState} from "react";
import "./News.css";
// https://newsapi.org/v2/everything?q=pollutions&apikey=226cb84b76db43dcb0b55516711accb2

function News() {

  const [user, setUser] = useState([]);

  const fetchData = () => {
    return fetch("https://newsapi.org/v2/everything?q=pollutions&apikey=226cb84b76db43dcb0b55516711accb2")
          .then((response) => response.json())
          .then((data) => setUser(data));
  }
  

  useEffect(() => {
    fetchData();
  },[])


  return (
    <>
      <h1>{user.author}</h1>
    </>
  );
}

export default News;
