import React, { useEffect, useState } from 'react'
import './Main.css'
import Modal from './Modal';
// import main1img from '../Utilities/logo.jpg'


const [apiData, setApiData] = useState({});
const [getState, setGetState] = useState('maharashtra');
const [state, setState] = useState('maharashtra');

const apiKey = "8b478ad7f6928c16526afbf709afae1e";
const apiUrl = `https://api.openweathermap.org/data/2.5/weather?q=${state}&appid=${apiKey}`;

useEffect(() => {
  fetch(apiUrl)
    .then((res) => res.json())
    .then((data) => setApiData(data));
}, [apiUrl]);

const inputHandler = (event) => {
  setGetState(event.target.value);
};

const submitHandler = () => {
  setState(getState);
};

const kelvinToFarenheit = (k) => {
  return (k - 273.15).toFixed(2);
};



function Main() { 

  // const BASE_KEY = "8b478ad7f6928c16526afbf709afae1e";

  const [openModal, setOpenModal] = useState(false);




  return (
   
    <div className='main'>

      <div className='main1'>
        <div className='main1Box'>
          <div className='main1box-1'>
            <p className='mainPara'>Welcome to Urban resolve...<br></br>
            let's make our city more livable by reporting all the issues...</p>

            <button className='main1Button' onClick={ () => setOpenModal(true) }>Report the problem</button>
            {openModal && <Modal closeModal={setOpenModal} />}
          
          </div>
          <div className='main1Box-2'>
            <img className='mainImg' src='https://cdn3d.iconscout.com/3d/free/thumb/react-native-5562339-4642743.png' alt='main page logo' ></img>
          </div>
        </div>
      </div>
  
      

      <div className='main2'>

        <h1 className='main2Heading'>Check the Weather of your city</h1>

        <input
            className='searchAQI'
            type="text"
            autoComplete="off"
            placeholder="Search your location..."
            onChange={inputHandler}
            value={getState}
          />
          <button className="button" onClick={submitHandler}>
          Search
        </button>

        <div className='aqiData'>
          <div className='location'>
            <div className='city'>Delhi</div>
            <div className='date'>sunday 10 2023</div>
          </div>
          <div className='current'>
            <div className='temp'>{kelvinToFarenheit(apiData.main.temp)}&deg; C</div>
            <div className='weather'>Mostly Sunny</div>
          </div>
          <div className='current'>

          </div>
        </div>

      </div>


      <div className='main3'>

      <h1>News related to pollution and clean energy</h1>

      </div>


      <div className='main4'>

      <h1>Insights into preserving the planet by reducing carbon footprint</h1>

      </div>


    </div>
  )
}

export default Main;
