import React from 'react'
import './Main.css'
import main1img from '../Utilities/logo.jpg'

async function getForecast(lat, lon) {
  const endpoint = 'https://api.windy.com/api/point-forecast/v2';
  const key = 'fdxSjMZoGDyfOPE04ZWDf93vuiEG62oL';
  const model = 'gfs';
  const parameters = ['wind', 'dewpoint', 'rh', 'pressure'];
  const levels = ['surface', '800h', '300h'];

  const requestBody = {
    lat,
    lon,
    model,
    parameters,
    levels,
    key
  };

  try {
    const response = await fetch(endpoint, {
      method: 'POST',
      body: JSON.stringify(requestBody),
      headers: { 'Content-Type': 'application/json' },
    });

    const data = await response.json();
    return data;
  } catch (error) {
    console.error(error);
  }
}

// Example usage
const lat = 49.809;
const lon = 16.787;
const forecast = getForecast(lat, lon);
console.log(forecast);



function Main() { 
  return (
    <div className='main'> 


      <div className='main1'>
        <div className='main1Box'>
          <div className='main1box-1'>
            <p className='mainPara'>Welcome to Urban resolve...<br></br>
            let's make our city more livable by reporting all the issues...</p>
            <button className='main1Button'>Report an issue</button>
          </div>
          <div className='main1Box-2'>
            <img className='mainImg' src={main1img} alt='main page logo' ></img>
          </div>
        </div>
      </div>

      <div className='main2'>

        <h1>Check the AQI of your city</h1>
        <input type="text" id="city-search" placeholder="Enter city name" />
        <button type="button" id="search-btn">Search</button>
      </div>


      <div className='main3'>

      <h1>News related to pollution and clean energy</h1>

      </div>


      <div className='main4'>

      <h1>Insights into preserving the planet by reducing carbon footprint</h1>

      </div>


    </div>
  )
}

export default Main
