import React, { useEffect, useState } from "react";
import "./News.css";


function News() {

  const url ="https://newsapi.org/v2/everything?q=pollution&apikey=226cb84b76db43dcb0b55516711accb2&pageSize=6";

  const [articles, setArticles] = useState([]);

  const news = async () => {
    try {
      const response = await fetch(url);
      const data = await response.json();
      setArticles(data.articles);
    } catch (error) {
        console.log(error);
    }
  };


useEffect(() => {
    news();
  }, []);

  return (
    <>
      {
      articles.map((element) => {
        return(
          // <div className="container">
          //   <div className="row">
          //     <img className="newsImg" src={element.urlToImage}/>
          //   <div className="col">
          //     <p>{element.title}</p>
          //     <p>Source: {element.source.name? element.source.name: "Source unknown"}
          //     <a href={element.url} target="_blank" rel="noreferrer" className="btn btn-sm btn-dark">Read More</a></p>
          //   </div>
          //   </div>
          // </div>
                      <div className='my-3'>
                      <div className="card">
                          
                          <img className="card-img-top" src={element.urlToImage} alt="..." />
                          <div className="card-body">
                              <h5 className="card-title"> {element.title}
                              </h5>
                              <a href={element.url} target="_blank" rel="noreferrer" className="btn btn-sm btn-dark">Read More</a>
                          </div>
                      </div>
                  </div>
        )
      })
      }
    
    </>  
  );
}

export default News;
