import React, { useEffect, useState } from "react";
import "./News.css";


function News() {

  const url ="https://newsapi.org/v2/everything?q=pollution&apikey=226cb84b76db43dcb0b55516711accb2&pageSize=3";

  const [articles, setArticles] = useState([]);

  const news = async () => {
    try {
      const response = await fetch(url);
      const data = await response.json();
      setArticles(data.articles);
    } catch (error) {
        console.log(error);
    }
  };


useEffect(() => {
    news();
  }, []);

  return (
    <>
      {
      articles.map((element) => {
        return(
          <div className='my-3'>
          <div className="card">
              
              <img className="card-img-top" src={!imageUrl ? "https://guwahatiplus.com/public/web/images/default-news.png" : imageUrl} alt="..." />
              <div className="card-body">
                  <h5 className="card-title"> {title}
                  </h5>
                  <p className="card-text"> {description} </p>
                  <p className="card-text"><small className="text-danger">By <strong>{author ? author : "unknown"}</strong>  on <strong> {new Date(date).toGMTString()} </strong></small></p>
                  <a href={newsUrl} target="_blank" rel="noreferrer" className="btn btn-sm btn-dark">Read More</a>
              </div>
          </div>
      </div>
        )
      })
      }
    
    </>  
  );
}

export default News;
