import React, {useState} from "react";
import "./News.css";

function News() {


  const [articles, setArticles] = useState([]);


  const fetchMoreData = async () => {
    const url = `https://newsapi.org/v2/top-headlines?country=IN&category=sports&apikey=226cb84b76db43dcb0b55516711accb2`;
    let data = await fetch(url);
    let parsedData = await data.json();

    setArticles(articles.concat(parsedData.articles))
    // setTotalResults(parsedData.totalResults)
}


  return (
    <>
      <div className="container">
        <div className="row">
          {articles.map((element) => {
            return (
              <div className="col-md-4" key={element.url}>
                
                  title={element.title ? element.title : ""}
                  description={element.description ? element.description : ""}
                  imageUrl={element.urlToImage}
                  newsUrl={element.url}
                  author={element.author}
                  date={element.publishedAt}
                
              </div>
            );
          })}
        </div>
      </div>
      <div className='my-3'>
                <div className="card">
                    
                    <img className="card-img-top" src={!imageUrl ? "https://guwahatiplus.com/public/web/images/default-news.png" : imageUrl} alt="..." />
                    <div className="card-body">
                        <h5 className="card-title"> {title}
                        </h5>
                        <p className="card-text"> {description} </p>
                        <p className="card-text"><small className="text-danger">By <strong>{author ? author : "unknown"}</strong>  on <strong> {new Date(date).toGMTString()} </strong></small></p>
                        <a href={newsUrl} target="_blank" rel="noreferrer" className="btn btn-sm btn-dark">Read More</a>
                    </div>
                </div>
            </div>
    </>
  );
}

export default News;
